mapper.select:
SELECT c.question_id, c.userId, c.title, c.category_id, cc.category_name, TO_CHAR(c.created_date, 'YYYY-MM-DD') created_date, c.is_public, c.saveFilename, c.originalFilename FROM sleep_clinic_question c JOIN member1 m ON c.userId = m.userId LEFT JOIN sleep_clinic_category cc ON c.category_id = cc.category_id
AND cc.category_name = #{category}
ORDER BY c.question_id DESC OFFSET #{offset} ROWS FETCH FIRST #{size} ROWS ONLY

mapper.update:
UPDATE sleep_clinic_question SET title=#{title}, content=#{content},
	category_id=#{category_id}, is_public=#{is_public},
	saveFilename=#{saveFilename, jdbcType=VARCHAR},
	originalFilename=#{originalFilename, jdbcType=VARCHAR}
WHERE question_id = #{question_id}

mapper.select:
select nvl(count(*), 0)
from sleep_clinic_answer
where question_id=#{question_id}
