mapper.update:
UPDATE FAQ
SET QUESTION = #{question},
    ANSWER = #{answer},
    UPDATED_DATE = SYSDATE,
    CATEGORY = #{category}
WHERE FAQ_NUM = #{faq_Num}

mapper.select:
SELECT FAQ_NUM, QUESTION, ANSWER, CASE WHEN UPDATED_DATE IS NOT NULL THEN UPDATED_DATE ELSE CREATED_DATE END AS DISPLAY_DATE, CATEGORY, IS_ACTIVE FROM FAQ WHERE IS_ACTIVE = 1
AND CATEGORY = #{category}
ORDER BY FAQ_NUM DESC
